import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useQuery } from "@apollo/client";
import { useStoreContext } from "../../utils/GlobalState";
import {
  UPDATE_WOMEN_PROUDUCTS,
  UPDATE_CURRENT_WOMEN_PROUDUCTS,
} from "../../utils/actions";
import { QUERY_WOMENCATEGORIES } from "../../utils/queries";
import { idbPromise } from "../../utils/helpers";

function ProductItem() {
  const [state, dispatch] = useStoreContext();

  const { womenProductItems } = state;

  const { loading, data: womenProductItemsData } = useQuery(
    QUERY_WOMENCATEGORIES
  );

  useEffect(() => {
    if (womenProductItemsData) {
      // Assuming womenProductItemsData contains an array of products
      dispatch({
        type: UPDATE_WOMEN_PROUDUCTS,
        products: womenProductItemsData.womenProductItems, // Access the correct property
      });

      // Seed the data into IndexedDB
      womenProductItemsData.womenProductItems.forEach((product) => {
        idbPromise("products", "put", product);
      });
    } else if (!loading) {
      // If data is not available, retrieve it from IndexedDB
      idbPromise("products", "get").then((products) => {
        dispatch({
          type: UPDATE_WOMEN_PROUDUCTS,
          products: products,
        });
      });
    }
  }, [womenProductItemsData, loading, dispatch]);

  const handleClick = (id) => {
    dispatch({
      type: UPDATE_CURRENT_WOMEN_PROUDUCTS,
      currentproduct: id,
    });
  };

  return (
    <div>
      <div className="ProductItem-container d-flex justify-content-center align-items-center m-5 hstack gap-5">
        {womenProductItems.map((item) => (
          <Link to="/ProductDetails" key={item._id}>
            <button
              onClick={() => {
                handleClick(item._id);
              }}
              className="ProductItem-container-button p-5 m-5 "
            >
              {item.name}
            </button>
          </Link>
        ))}
      </div>
    </div>
  );
}
export default ProductItem;
