import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useQuery } from "@apollo/client";
import { useStoreContext } from "../../utils/GlobalState";
import {
  UPDATE_CATEGORIES,
  UPDATE_CURRENT_CATEGORY,
} from "../../utils/actions";
import { QUERY_CATEGORIES } from "../../utils/queries";
import { idbPromise } from "../../utils/helpers";
import "../../scss/components/CategoryMenu.scss";
function CategoryMenu() {
  const [state, dispatch] = useStoreContext();

  const { categories } = state;

  const { loading, data: categoriesData } = useQuery(QUERY_CATEGORIES);

  useEffect(() => {
    if (categoriesData) {
      dispatch({
        type: UPDATE_CATEGORIES,
        categories: categoriesData.categories,
      });
      categoriesData.categories.forEach((categories) => {
        idbPromise("categories", "put", categories);
      });
    } else if (!loading) {
      idbPromise("categories", "get").then((categories) => {
        dispatch({
          type: UPDATE_CATEGORIES,
          categories: categories,
        });
      });
    }
  }, [categoriesData, loading, dispatch]);

  const handleClick = (id) => {
    dispatch({
      type: UPDATE_CURRENT_CATEGORY,
      currentCategories: id,
    });
  };

  return (
    <div>
      <div className="CategoryMenu-container d-flex justify-content-center align-items-center m-5 hstack gap-5">
        {categories.map((item) => (
          <Link to="/ProductList">
            <button
              key={item._id}
              onClick={() => {
                handleClick(item._id);
              }}
              className="CategoryMenu-container-button p-5 m-5 "
            >
              {item.name}
            </button>
          </Link>
        ))}
      </div>
    </div>
  );
}
export default CategoryMenu;
