import React from "react";
import { useParams, Link } from "react-router-dom";
import "../../scss/pages/ProductDetails.scss";
import { useEffect } from "react";
import { useStoreContext } from "../../utils/GlobalState";
import { useQuery } from "@apollo/client";
import { idbPromise } from "../../utils/helpers";
import { QUERY_ALL_PRODUCTS } from "../../utils/queries";
import { UPDATE_PRODUCTS, UPDATE_CURRENT_PRODUCTS } from "../../utils/actions";

function ProductDetailsRight() {
  const { womenProductItems } = useParams();
  const [state, dispatch] = useStoreContext();
  const { products } = state;
  const { loading, data: productsData } = useQuery(QUERY_ALL_PRODUCTS);
  // useEffect(() => {
  //   if (productsData) {
  //     dispatch({
  //       type: UPDATE_PRODUCTS,
  //       productsData: productsData.products,
  //     });
  //   } else if (!loading) {
  //     idbPromise("products", "get").then((productsData) => {
  //       dispatch({
  //         type: UPDATE_PRODUCTS,
  //         productsData: productsData,
  //       });
  //     });
  //   }
  // }, [productsData, loading, dispatch]);
  // const handleClick = (id) => {
  //   dispatch({
  //     type: UPDATE_CURRENT_PRODUCTS,
  //     currentproductsData: id,
  //   });
  // };
  console.log("productsData:", productsData);

  const renderProductCards = () => {
    if (loading) {
      return <p>Loading...</p>;
    }

    if (
      !productsData ||
      !productsData.products ||
      productsData.products.length === 0
    ) {
      return <p>No products available.</p>;
    }

    const filteredProducts = productsData.products.filter(
      (product) => product.name === womenProductItems
    );

    if (filteredProducts.length === 0) {
      return <p>No products available for this category.</p>;
    }

    return filteredProducts.map((product) => (
      <div className="card p-1 m-3 d-inline-block" key={product._id}>
        <Link to={`/products/${product._id}`}>
          <img
            src={`/public/images/Women/${product.name}/women-${product.name}-black.jpg`}
            alt={product.name}
          />
        </Link>
        <div className="card-body">
          <ul className="list-unstyled">
            <li>name: {product.name}</li>
            <li>price: ${product.price}</li>
            <li>color: {product.color}</li>
            <li>size: {product.size}</li>
          </ul>
          <button>
            <a href="#">Add to cart</a>
          </button>
        </div>
      </div>
    ));
  };

  return <div>{renderProductCards()}</div>;
}

export default ProductDetailsRight;
