import React from "react";
import Auth from "../../utils/auth";
import { Link } from "react-router-dom";
import "../../scss/components/Nav.scss";
import shoppingbagicon from "../Nav/shopping-bag.svg";
function Nav() {
  function showNavigation() {
    if (Auth.loggedIn()) {
      return (
        <ul className="d-flex hstack gap-4 flex-row pe-5 me-1 list-unstyled">
          <li className="mx-1 ">
            <Link to="/orderHistory" className="text-decoration-none">
              Order History
            </Link>
          </li>
          <li className="mx-1">
            <a
              href="/"
              className="text-decoration-none"
              onClick={() => Auth.logout()}
            >
              Logout
            </a>
          </li>
        </ul>
      );
    } else {
      return (
        <ul className=" d-flex hstack gap-4 flex-row pe-5 me-1 list-unstyled">
          <li className="mx-1">
            <Link to="/signup" className="text-decoration-none">
              Signup
            </Link>
          </li>
          <li className="mx-1">
            <Link to="/login" className="text-decoration-none">
              Login
            </Link>
          </li>
        </ul>
      );
    }
  }

  return (
    <header className="Nav d-flex justify-content-between align-items-center flex-row px-5 bg-info">
      <h1>
        <Link to="/" className="text-decoration-none">
          {/* <span role="img" aria-label="shopping bag">
            üõçÔ∏è
          </span> */}
          <img
            src={shoppingbagicon}
            alt="shopping-cart svg"
            className="me-1 pb-1"
          ></img>
          Choice
        </Link>
      </h1>

      <nav>{showNavigation()}</nav>
    </header>
  );
}

export default Nav;
