import React from "react";
import { useParams, Link } from "react-router-dom";
import "../scss/pages/ProductDetails.scss";
import { useEffect } from "react";
import { useStoreContext } from "../utils/GlobalState";
import { useQuery } from "@apollo/client";
import { idbPromise } from "../utils/helpers";
import { QUERY_ALL_PRODUCTS } from "../utils/queries";
import { UPDATE_PRODUCTS, UPDATE_CURRENT_PRODUCTS } from "../utils/actions";
function ProductDetails() {
  const [state, dispatch] = useStoreContext();
  const { womenProductItems } = useParams();
  const { products } = state;
  const { loading, data: productsData } = useQuery(QUERY_ALL_PRODUCTS);
  // Define the updateRangeValues function here
  const updateRangeValues = () => {
    const priceRange = document.getElementById("price-range");
    const rangeValueMin = document.getElementById("range-value-min");
    const rangeValueMax = document.getElementById("range-value-max");

    if (priceRange && rangeValueMin && rangeValueMax) {
      const [min, max] = priceRange.value.split(",");
      rangeValueMin.textContent = min;
      rangeValueMax.textContent = max;
    }
  };

  useEffect(() => {
    const priceRange = document.getElementById("price-range");

    if (priceRange) {
      // Add the event listener using the updateRangeValues function
      priceRange.addEventListener("input", updateRangeValues);
    }

    // Clean up the event listener when the component unmounts
    return () => {
      if (priceRange) {
        priceRange.removeEventListener("input", updateRangeValues);
      }
    };
  }, []);
  useEffect(() => {
    if (productsData) {
      dispatch({
        type: UPDATE_PRODUCTS,
        productsData: productsData.products,
      });
    } else if (!loading) {
      idbPromise("products", "get").then((productsData) => {
        dispatch({
          type: UPDATE_PRODUCTS,
          productsData: productsData,
        });
      });
    }
  }, [productsData, loading, dispatch]);
  const handleClick = (id) => {
    dispatch({
      type: UPDATE_CURRENT_PRODUCTS,
      currentproductsData: id,
    });
  };
  console.log("productsData:", productsData);
  console.log("product:", products);
  console.log("loading:", loading);

  ////////////////for rendering t-shirt, jacket, sweatshirt and bottoms////////////////////
  let productToRender;
  if (products && products.name === "jacket") {
    productToRender = (
      <div className="card p-1 m-3">
        <Link to={`/products/jacket`}>
          <img
            src={`/public/images/Women/jacket/${products.name}-black`} // Dynamic image path
            alt={products.name}
          />
        </Link>
        <div className="card-body">
          <ul className="list-unstyled">
            <li>name: {products.name}</li>
            <li>price: ${products.price}</li>
          </ul>
          <button>
            <a href="#">Add to card</a>
          </button>
        </div>
      </div>
    );
  } else if (products && products.name === "t-shirt") {
    // Render t-shirt product similarly
    productToRender = (
      <div className="card p-1 m-3">
        <Link to={`/products/t-shirt`}>
          <img
            src={`/public/images/Women/t-shirt/${products.name}-black`} // Dynamic image path
            alt={products.name}
          />
