import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useQuery } from "@apollo/client";
import { useStoreContext } from "../../utils/GlobalState";
import {
  UPDATE_PROUDUCTSS,
  UPDATE_CURRENT_PROUDUCTSS,
} from "../../utils/actions";
import { QUERY_WOMENCATEGORIES } from "../../utils/queries";
import { idbPromise } from "../../utils/helpers";

function ProductItem() {
  const [state, dispatch] = useStoreContext();

  const { womenProductItems } = state;

  const { loading, data: ProductData } = useQuery(QUERY_WOMENCATEGORIES);

  useEffect(() => {
    if (ProductData) {
      dispatch({
        type: UPDATE_PROUDUCTSS,
        products: ProductData.products,
      });
      ProductData.categories.forEach((product) => {
        idbPromise("products", "put", product);
      });
    } else if (!loading) {
      idbPromise("products", "get").then((product) => {
        dispatch({
          type: UPDATE_PROUDUCTSS,
          products: product,
        });
      });
    }
  }, [ProductData, loading, dispatch]);

  const handleClick = (id) => {
    dispatch({
      type: UPDATE_CURRENT_PROUDUCTSS,
      currentproduct: id,
    });
  };

  return (
    <div>
      <div className="ProductItem-container d-flex justify-content-center align-items-center m-5 hstack gap-5">
        {womenProductItems.map((item) => (
          <Link to="/ProductDetails">
            <button
              key={item._id}
              onClick={() => {
                handleClick(item._id);
              }}
              className="ProductItem-container-button p-5 m-5 "
            >
              {item.name}
            </button>
          </Link>
        ))}
      </div>
    </div>
  );
}
export default ProductItem;
