import React, { useEffect } from "react";
import ProductItemMen from "../components/ProductItemMen";
import { useStoreContext } from "../utils/GlobalState";
import { Link } from "react-router-dom";
import { UPDATE_PRODUCTS } from "../utils/actions";
import { useQuery } from "@apollo/client";
import { QUERY_ALL_PRODUCTS } from "../utils/queries";
import { idbPromise } from "../utils/helpers";
// import spinner from "../assets/spinner.gif";
import "../scss/pages/ProductList.scss";
function ProductList() {
  const [state, dispatch] = useStoreContext();

  const { currentCategory } = state;

  const { loading, data } = useQuery(QUERY_ALL_PRODUCTS);

  useEffect(() => {
    if (data) {
      dispatch({
        type: UPDATE_PRODUCTS,
        products: data.products,
      });
      data.products.forEach((product) => {
        idbPromise("products", "put", product);
      });
    } else if (!loading) {
      idbPromise("products", "get").then((products) => {
        dispatch({
          type: UPDATE_PRODUCTS,
          products: products,
        });
      });
    }
  }, [data, loading, dispatch]);

  function filterProducts() {
    if (!currentCategory) {
      return state.products;
    }

    return state.products.filter(
      (product) => product.category._id === currentCategory
    );
  }

  return (
    <div className=" ProductList my-2">
      <section className="ProductList-header">
        <video className="ProductList-header_Video" autoPlay muted loop>
          <source src="/videos/ProductDetailsMen.mp4" type="video/mp4"></source>
        </video>
        <div className="ProductList-header-text">
          <h1 className=" ProductList-header-text-1 text-white">
            ELEVATE YOUR STYLE,
          </h1>
          <h1 className="ProductList-header-text-2 text-white">
            WHERE FASHION MEETS INDIVISUALITY
          </h1>
          <button className="ProductList-header-text-3  p-2 text-warning border rounded-2">
            <a
              href="/"
              alt="link to home page"
              className="text-decoration-none "
            >
              WITH <strong>CHOICE</strong>
            </a>
          </button>
        </div>
      </section>

      <section className="ProductList-main">
        <h1 className="text-center pt-6 text-primary ">Choose By Items</h1>
        <ProductItemMen />
      </section>

      <section className="ProductList-details m-3 p-3 ">
        <div className="ProductList-details-detail   col-md-6 ">
          <h2 className="mb-5 mt-7">The Choice Commitment to Quality </h2>
          <p className="mt-5 ms-3 me-7 lh-lg">
            At Choice, the commitment to exceptional quality extends beyond
            clothing—it's a way of life. The men's products offered by Choice
            are a testament to this dedication, embodying both style and
            substance. Crafted with precision and an eye for timeless design,
            Choice provides a diverse array of apparel and accessories that
            cater to the modern man's needs. Whether you're looking for classic
            formalwear or stylish casual pieces, you can trust that Choice
            products are crafted to meet the highest standards of quality. From
            luxurious fabrics to impeccable stitching, each item is a reflection
            of Choice's passion for delivering excellence. With Choice, you're
            not just buying clothes; you're investing in a level of quality that
            enhances your personal style, leaving a lasting impression wherever
            life takes you.
          </p>
          <button className="btn btn-primary  mt-3">
            {" "}
            <a href="#" className="text-decoration-none text-white">
              learn more
            </a>
          </button>
        </div>
        <div className="ProductList-details-picture col-md-6">
          <img
            src="./images/ProductList-pictures/men-green-1.jpg"
            alt="joyful-man"
          ></img>
        </div>
      </section>
    </div>
  );
}

export default ProductList;
